import React, { useEffect, useState } from "react";

import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Card from "react-bootstrap/Card";

import Chart from "./chart";
import SingleData from "../components/SingleData";

import "../Styles/country.css";

const Country = (props) => {
  const [name, setName] = useState("Canada");
  const [data, setData] = useState([]);

  const [xdata, setXData] = useState([]);
  const [ydata, setYData] = useState([]);
  const [xdataD, setXDataD] = useState([]);
  const [ydataD, setYDataD] = useState([]);
  const [xdataR, setXDataR] = useState([]);
  const [ydataR, setYDataR] = useState([]);

  const [cases, setCases] = useState(0);
  const [critical, setCritical] = useState(0);
  const [deaths, setDeaths] = useState(0);
  const [recovered, setRecovered] = useState(0);

  const updateGraph = (cat, hist) => {
    fetch(`/country-graph-data?category=${cat}&history=${hist}&country=${name}`)
      .then((res) => res.json())
      .then((Jsondata) => {
        setXData(Object.keys(Jsondata));
        setYData(Object.values(Jsondata));
      });
  };

  const fetchCurrentData = (callback) => {
    fetch(`/country-data?country=${name}`)
      .then((res) => res.json())
      .then((Jsondata) => {
        callback(Jsondata);
      });
  };

  useEffect(() => {
    try {
      setName(props.location.props.name);
      //cases graph data
      fetch(
        "/country-graph-data?category=cases&history=30&country=" +
          props.location.props.name
      )
        .then((res) => res.json())
        .then((Jsondata) => {
          setXData(Object.keys(Jsondata));
          setYData(Object.values(Jsondata));
        });
      //deaths graph data
      fetch(
        "/country-graph-data?category=deaths&history=30&country=" +
          props.location.props.name
      )
        .then((res) => res.json())
        .then((Jsondata) => {
          setXDataD(Object.keys(Jsondata));
          setYDataD(Object.values(Jsondata));
        });
      //recovered graphdata
      fetch(
        "/country-graph-data?category=recovered&history=30&country=" +
          props.location.props.name
      )
        .then((res) => res.json())
        .then((Jsondata) => {
          setXDataR(Object.keys(Jsondata));
          setYDataR(Object.values(Jsondata));
        });
    } catch (err) {
      setName(name);
      fetch("/country-graph-data?category=cases&history=30&country=Canada")
        .then((res) => res.json())
        .then((Jsondata) => {
          setData(Jsondata);
          setXData(Object.keys(Jsondata));
          setYData(Object.values(Jsondata));
        });
      //deaths graph data
      fetch("/country-graph-data?category=deaths&history=30&country=Canada")
        .then((res) => res.json())
        .then((Jsondata) => {
          setXDataD(Object.keys(Jsondata));
          setYDataD(Object.values(Jsondata));
        });
      //recovered graphdata
      fetch("/country-graph-data?category=recovered&history=30&country=Canada")
        .then((res) => res.json())
        .then((Jsondata) => {
          setXDataR(Object.keys(Jsondata));
          setYDataR(Object.values(Jsondata));
        });
    }

    fetchCurrentData((data) => {
      setCases(data.confirmed);
      setCritical(data.critical);
      setDeaths(data.deaths);
      setRecovered(data.recovered);
    });

    //fetch data using name
  }, [name]);

  return (
    <div className="country-page">
      <Row>
        <Col>
          <div>
            <Row>
              <Card className="country-name">
                <h1>{name}</h1>
              </Card>
            </Row>
            <Row>
              <Card className="main-data-card">
                <Row style={{ marginTop: "8%" }}>
                  <div className="main-data">
                    <h3>Total Cases</h3>
                    <h3 style={{ color: "white" }}>{cases}</h3>
                  </div>
                  <div className="main-data">
                    <h3>Critcal Cases</h3>
                    <h3 style={{ color: "orange" }}>{critical}</h3>
                  </div>
                </Row>
                <Row id="bottom-main-data-row">
                  <div className="main-data">
                    <h3>Deaths</h3>
                    <h3 style={{ color: "red" }}>{deaths}</h3>
                  </div>
                  <div className="main-data">
                    <h3>Recovered</h3>
                    <h3 style={{ color: "green" }}>{recovered}</h3>
                  </div>
                </Row>
              </Card>
            </Row>
            <Row>
              <Card className="main-data-card">
                <div>
                  <h3>30 Day History of Cases</h3>
                </div>
                <div
                  className="new-cases"
                  style={{
                    overflowY: "auto",
                    width: "400px",
                    background: "transparent",
                    margin: "auto",
                  }}
                >
                  {Object.keys(data)
                    .slice(-30)
                    .reverse()
                    .map((key, index) => (
                      <SingleData date={key} value={data[key]} />
                    ))}
                </div>
              </Card>
            </Row>
          </div>
        </Col>
        <Col>
          <Row>
            <Card className="main-chart">
              <div style={{ width: "90%", margin: "0 auto" }}>
                <Chart cat={"Cases"} x={xdata} y={ydata} />
                {/*Create little bar below to change chart data */}
              </div>
            </Card>
          </Row>

          <Row>
            <Card className="under-chart-data">
              <div style={{ width: "90%", margin: "auto auto" }}>
                <Chart cat={"Deaths"} x={xdataD} y={ydataD} />
                {/*Create little bar below to change chart data */}
              </div>
            </Card>
            <Card className="under-chart-data">
              <div style={{ width: "90%", margin: "auto auto" }}>
                <Chart cat={"Recovered"} x={xdataR} y={ydataR} />
                {/*Create little bar below to change chart data */}
              </div>
            </Card>
          </Row>
        </Col>
      </Row>
    </div>
  );
};

export default Country;
