import React, { useEffect, useState } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
const Country = (props) => {
  const [name, setName] = useState("Canada");
  const [xdata, setXData] = useState([]);
  const [ydata, setYData] = useState([]);
  const [data, setData] = useState([]);
  useEffect(() => {
    try {
      setName(props.location.props.name);
    } catch (err) {
      setName("Canada");
    }
    fetch("/country-graph-data?category=deaths&history=90&country=" + name)
      .then((res) => res.json())
      .then((Jsondata) => {
        setXData(Object.keys(Jsondata));
        setYData(Object.values(Jsondata));
        setData([Jsondata]);
      });
    //fetch data using name
  }, []);

  return (
    <div>
      <h1>Country: {name}</h1>
      <Bar
        data={data}
        width={100}
        height={50}
        options={{ maintainAspectRatio: false }}
      />
    </div>
  );
};

export default Country;
