import React, { useEffect, useState } from "react";
import { LineChart, Line, CartesianGrid, YAxis, XAxis } from "recharts";

const Country = (props) => {
  const [name, setName] = useState("Canada");
  const [xdata, setXData] = useState([]);
  const [ydata, setYData] = useState([]);
  const [data, setData] = useState([]);
  useEffect(() => {
    try {
      setName(props.location.props.name);
    } catch (err) {
      setName("Canada");
    }
    fetch("/country-graph-data?category=deaths&history=90&country=" + name)
      .then((res) => res.json())
      .then((Jsondata) => {
        setXData(Object.keys(Jsondata));
        setYData(Object.values(Jsondata));
        setData([Jsondata]);
      });
    //fetch data using name
  }, []);

  return (
    <div>
      <h1>Country: {name}</h1>
      <LineChart width={500} height={300} data={data}>
        <XAxis dataKey="name" />
        <YAxis />
        <CartesianGrid stroke="#eee" strokeDasharray="5 5" />
        <Line type="monotone" dataKey="uv" stroke="#8884d8" />
        <Line type="monotone" dataKey="pv" stroke="#82ca9d" />
      </LineChart>
    </div>
  );
};

export default Country;
